DROP TABLE IF EXISTS `wa_receipt`;
CREATE TABLE `wa_receipt`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `user_id` int(10) UNSIGNED NOT NULL COMMENT '出借人',
  `to_user_id` int(10) UNSIGNED NOT NULL COMMENT '借款方',
  `amount` decimal(10, 2) UNSIGNED NOT NULL COMMENT '欠款金额',
  `repayment_type` tinyint(1) UNSIGNED NOT NULL COMMENT '还款方式:1=一次性还本付息,2=分期还款',
  `rate` float(5, 2) NOT NULL COMMENT '年化利率',
  `start_date` date NULL DEFAULT NULL COMMENT '起始日期',
  `end_date` date NULL DEFAULT NULL COMMENT '还款日期',
  `reason` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '欠款原因',
  `mark` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '原因详情',
  `stage` int(11) NULL DEFAULT NULL COMMENT '分期期数',
  `stage_day` int(11) NULL DEFAULT NULL COMMENT '分期时长',
  `stage_amount` decimal(10, 2) NULL DEFAULT NULL COMMENT '每期应收',
  `status` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '状态:0=待确认,1=待还款,2=已逾期,3=已还款,4=已失效',
  `ordersn` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '订单编号',
  `pay_amount` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '支付金额',
  `pay_type` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '支付类型:0=无,1=微信',
  `clause_rule` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '条款协议',
  `borrow_rule` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '借款协议',
  `cert_rule` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '授权协议',
  `pay_time` datetime NULL DEFAULT NULL COMMENT '支付时间',
  `sign` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '签名',
  `clause_images` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '条款协议图片',
  `cert_images` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '授权协议图片',
  `borrow_images` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '借款协议图片',
  `interest` decimal(10, 2) UNSIGNED NULL DEFAULT NULL COMMENT '利息',
  `amount_and_interest` decimal(10, 2) NULL DEFAULT NULL COMMENT '本息',
  `repaid_amount` decimal(10, 2) NULL DEFAULT NULL COMMENT '已还金额',
  `outstanding_amount` decimal(10, 2) NULL DEFAULT NULL COMMENT '待还金额',
  `created_at` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `cancel_time` datetime NULL DEFAULT NULL COMMENT '取消时间',
  `updated_at` datetime NULL DEFAULT NULL COMMENT '更新时间',
  `deleted_at` datetime NULL DEFAULT NULL COMMENT '删除时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 49 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '借条' ROW_FORMAT = Dynamic;

