DROP TABLE IF EXISTS `wa_users_feedback`;
CREATE TABLE `wa_users_feedback`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `user_id` int(10) UNSIGNED NOT NULL COMMENT '用户',
  `field1` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '常用功能',
  `field2` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '满意度',
  `field3` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '希望的新功能',
  `field4` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '新功能原因',
  `field5` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '需要改进的功能',
  `field6` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '其他',
  `created_at` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 4 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '用户反馈' ROW_FORMAT = Dynamic;

